JavaScript Rendering:
function renderProjects() {
  const grid = document.getElementById('grid');
  window.PROJECTS.forEach(project => {
    const card = `<article class="bg-white rounded-lg shadow-paper">
      <div class="cover-${project.style_type}"></div>
      <h3>${project.title}</h3>
    </article>`;
    grid.innerHTML += card;
  });
}

