async function getLikes(projectId) {
  const response = await fetch(`https://api.counterapi.dev/v1/mkn501-symbiosis/project-${projectId}/`);
  return response.json().value;
}

async function incrementLike(projectId) {
  await fetch(`https://api.counterapi.dev/v1/mkn501-symbiosis/project-${projectId}/up`, {method: 'GET'});
}
